  var table_row_locator = storedVars['table_row_locator'];
  console.log(table_row_locator);
} catch (e) {
  // ReferenceError: storedVars is not defined
  // available only in storeEval 
}
table_row_locator = "${table_row_locator}";
console.log(table_row_locator);
var rows = document.querySelectorAll(table_row_locator);
var result = [];
for (row_cnt = 0; row_cnt != rows.length; row_cnt++) {
  var row = rows[row_cnt];
  if (row instanceof Element) {
    var cols = row.querySelectorAll('td');
    var col_num = 1;
    result.push(cols[col_num].innerHTML);
  }
}
console.log(result.join());

(function() {
  var table_row_locator = storedVars['table_row_locator'];
  var rows = window.document.querySelectorAll(table_row_locator);
  var result = [];
  for (row_cnt = 0; row_cnt != rows.length; row_cnt++) {
		var row = rows[row_cnt];
		if (row instanceof Element) {
			var cols = row.querySelectorAll('td');
			var col_num = 1;
			result.push(cols[col_num].innerHTML);
		}
	}
	return result;
})();
