tr.td:
var rows = document.querySelectorAll('div#example_wrapper table#example tbody tr.even');
var result = [];
for (row_cnt = 0; row_cnt != rows.length; row_cnt++) {
    var row = rows[row_cnt];
    if (row instanceof Element) {
        var cols = row.querySelectorAll('td');
        var col_num = 1;
        result.push(cols[col_num].innerHTML);
    }
}
console.log(result.join());

(function() {
    var rows = window.document.querySelectorAll('div#example_wrapper table#example tbody tr.even');
    var result = [];
    for (row_cnt = 0; row_cnt != rows.length; row_cnt++) {
        var row = rows[row_cnt];
        if (row instanceof Element) {
            var cols = row.querySelectorAll('td');
            var col_num = 1;
            result.push(cols[col_num].innerHTML);
        }
    }
    return result;
})();
